652:     depositorTotalStaked[_depositor] += _amount;
653:     earningPower[_beneficiary] += _amount;
654:     deposits[_depositId] = Deposit({
655:       balance: _amount,
656:       owner: _depositor,
657:       delegatee: _delegatee,
658:       beneficiary: _beneficiary
