661:     emit StakeDeposited(_depositor, _depositId, _amount, _amount);
662:     emit BeneficiaryAltered(_depositId, address(0), _beneficiary);
663:     emit DelegateeAltered(_depositId, address(0), _delegatee);
664:   }

